import React, { createContext, useState, useEffect } from "react";
import axios from "axios";

export const ApiContex = createContext();

const ApiContext = () => {

    const [mainText, subText] = useState([]);

    useEffect(() => {

        const getData = async () => {
            try {
                const res = await axios.get("http://127.0.0.1:8000/sample/",)
                console.log(res);
            } catch {
                console.log("error !!");
            }
        };

    });

    return (
        <ApiContex.Provider
            value={{
                mainText,
                subText,
            }}
        ></ApiContex.Provider>
    );

}

export default ApiContex;
